using System.Collections.Generic; using UnityEngine;  public class GridManager : MonoBehaviour {     public const int COLS = 6;     public const int ROWS = 3;     int[,] _gems = new int[COLS, ROWS];      void Start()     {          _gems[0, 0] = Random.Range(0, 9);         _gems[0, 1] = Random.Range(0, 9);         _gems[0, 2] = Random.Range(0, 9);         _gems[1, 0] = Random.Range(0, 9);         // _gems[2,1] = 5;          for (int x = 0; x < COLS; x++)         {             for (int y = 0; y < ROWS; y++)             {                 int color = Random.Range(0, 9);                 _gems[x, y] = color;             } // internal loop will go until it is finished          }          for(int x = 0; x < COLS; x++)        {             for (int y = 0; y < ROWS; y++)             {                 Debug.Log("Gem" + x + "," + y + ":" + _gems[x,y]); // using the plus sign to stick the strings together - to log the message             }        }       }      void Update()     {      } }  
